class HotelDisplay extends React.Component{
    destinationData = GetDestinationData(this.props.DestinationData);
    HotelList = GetHotelList(this.destinationData);

    state={
        hotellist: this.HotelList,
        pageSize: 20,
        currentPage: 1,
    };


    handleSelect = hotel =>{
        console.log(hotel);
        this.props.GetHotel(hotel);
    };

    handlePageChange = page =>{
        this.setState({currentPage:page})
    };
   
    render(){
        if (this.state.hotellist.length === 0){
        return <p>Sorry there are no hotels available</p>};

        const hotelListSlice = paginate(this.state.hotellist, this.state.currentPage, this.state.pageSize);

        return(
        <React.Fragment>
            <p class="position-absolute top-10 start-40">Search result: {this.state.hotellist.length} hotels.</p>
            <table className='table'>
                <thead>
                    <tr>
                        <th width="250">Picture</th>
                        <th width="500">Hotel Name</th>
                        <th width="180">Price</th>
                        <th width="100"> </th>
                    </tr>
                </thead>
                <tbody>
                    {hotelListSlice.map(hotel =>
                    <tr key={hotel.id}>
                        <td width="250" height="200"><img src={`${hotel.image_details.prefix}${hotel.default_image_index}${hotel.image_details.suffix}`} width="200" height="200" alt={`${hotel.image_details.prefix}${hotel.default_image_index}${hotel.image_details.suffix}`}/></td>
                        <td>{hotel.name}</td>
                        <td>99</td>
                        <td><button onClick={()=>this.handleSelect(hotel)} type="button" class="btn btn-primary" id="selbtn">Select</button></td>
                    </tr>
                    )}
                    
                </tbody>
            </table>
            <Pagination 
                itemsCount={this.state.hotellist.length} 
                pageSize={this.state.pageSize} 
                currentPage = {this.state.currentPage}
                onPageChange={this.handlePageChange}
                />
        </React.Fragment>
        
                    )}
}


export default HotelDisplay;
