//     const res = await fetch(url);
//     const hotelDetails = await res.json();
//     return hotelDetails;
// };

const HotelRoomDetails = (props) => {  

    const [hotel, setHotel] = useState(
        {
            "imageCount": null,
            "latitude": -3.745,
            "longitude": -38.523,
            "name": null,
            "address": null,
            "rating": 0,
            "trustyou": null,
            "categories": null,
            "amenities_ratings": [],
            "description": " ",
            "amenities": {},
            "original_metadata": null,
            "rooms": [],
        }
    );  
    const [loaded, setLoaded] = useState(false); 

    useEffect(() => {
        async function init() {
            if (props.hotelID != null) {
                const hotelInfo = await GetHotelDetails(props.hotelID);
                setHotel(hotelInfo);
                setLoaded(true);
            }
        }
         init();
     }, []);

    return (
        <div>
            <Row>
                <Col span={10} offset={0}>
                    { loaded &&
                        <Carousel autoplay arrows nextArrow={<RightOutlined />} prevArrow={<LeftOutlined/>}>
                                { Array(hotel.image_details.count).fill(null).map((value, index) => 
                                    <div> 
                                        <Image style={contentStyle} 
                                        src={`${hotel.image_details.prefix}${index}${hotel.image_details.suffix}`}
                                        fallback="data:image/png;base64,"
                                        /> 
                                    </div> 
                                    )} 
                        </Carousel>
                    } 
                    { !loaded &&
                        <Carousel autoplay>
                            <div>
                                <Image
                                    src="error"
                                    fallback="data:image/png;base64,"
                                />
                            </div>
                        </Carousel>
                    }
                </Col>
                <Col span={ 13 } offset={ 1 }>
                    <Card style={{ height:"100%" }}>
                        <div style={{ height:'100%', boxSizing: 'border-box', textAlign: 'left' }}>
                            {<h3> <b> {hotel.name} </b></h3>}
                            <h4 style={{ display:"block", fontWeight: "bold",fontSize: "large", marginBottom:10 }}>{hotel.address}</h4>
                            <Row type="flex" align="middle">
                                <span style={{ fontSize: "large", marginRight: 6 }}>Hotel Rating: </span>
                                {Array(Math.round(hotel.rating)).fill(null).map((value, index) => (
                                    <StarFilled style={{ margin: 0, padding: 0, verticalAlign: 'middle', color:'orange' }}  key={index}/>))}
                            </Row>
                        </div>
                    </Card>
                </Col>
            </Row>
            <Row style={{marginTop: 24}}>
                <Card title= {<h3> <b>  Hotel Overview </b></h3>} style={{height:"100%"}}>
                    <div style={{height:'100%', boxSizing: 'border-box', textAlign: 'left'}}>
                        <Row>
                            {parse(sanitizeHtml(hotel.description))}
                        </Row>
                    </div>
                </Card>
            </Row>
            <Row style={{ marginTop: 24 }}>
                <Card style={{borderheight:"100%", width:"100%"}}>
                    {<h3> <b>  Room Options </b></h3>}
                </Card>
            </Row>
            <Row style={{ marginTop: 24 }}>
                <RoomCard hotelroom = { hotel.rooms }></RoomCard>
            </Row>
            <Row style={{ marginTop: 24 }}>
                <Card style={{borderheight:"100%", width:"100%"}}>
                    {<h3> <b>  Map </b></h3>}
                </Card>
            </Row>
            <Row>
                <HotelMap Long={hotel.longitude} Lat={hotel.latitude}></HotelMap>
            </Row>

        </div>
    )

}

export default HotelRoomDetails;